import React, {Image, useState, useEffect} from 'react';
import axios from 'axios';
import {useParams} from 'react-router-dom';
import { divide } from 'lodash';

const box = {
    width: '25rem',
    maxHeight: '40rem',
    margin: '80px',
    boxShadow: '2px 2px 2px 2px #cccccc',
}

const title = {
    width: '25rem',
    maxHeight: '25rem',
    margin: '80px',
    boxShadow: '2px 2px 2px 2px #cccccc',
}

function Product(props) {
    const {id} = useParams();
    const [product, setProduct] = useState({})
    console.log(id)

    useEffect(() => {
        axios.get(`http://localhost:5001/product/${id}`)

            .then(response => {
                console.log("response in useEffect Product.js")
                console.log(response.data.productList)
                setProduct(response.data.productList[0])
            })

    }, [id])


    console.log(product.title)
    return (
        <div>

            <h1>{product.title}</h1>
            <img style={box} src={product.image}/> 

        </div>


    );
}


export default Product;
