import React, {useState} from 'react';


function Form() {   
    const [product, setProduct] = useState(
        {  
            title: '',
            description: '',
            location: '',
            category: '',
            contactInfo: ''

        }
    );

    function handleChange(event) {
        const { name, value } = event.target;
        if (name === "job")
        setProduct(
            {name: product['name'], job: value}
        );
        else
            setProduct(
            {name: value, job: product['job']}
            );   
    }

    return (
        <form>
        <label htmlFor="name">Name</label>
        <input
            type="text"
            name="name"
            id="name"
            value={product.name}
            onChange={handleChange} />
        <label htmlFor="job">Job</label>
        <input
            type="text"
            name="job"
            id="job"
            value={product.job}
            onChange={handleChange} />
        </form>
    ); 

}
export default Form;
