import React, {useState} from 'react';


function Form(props) {   
    const [productData, setProduct] = useState(
        {  
            title: '',
            description: '',
            location: '',
            category: '',
            contactInfo: ''
        }
    );

    function handleChange(event) {

        const {name, value} = event.target;

        if (name === "title") {
            setProduct(
                {name: product['title'], title: value}
            );
        }

        else {
            setProduct(
                {name: value, job: product['job']}
            ); 
        }

    }

    function submitForm() {
        props.handleSubmit(product);
        setProduct({title: '',
                    description: '',
                    location: '',
                    category: '',
                    contactInfo: ''});
      }

    return (
        <form>

        <label htmlFor="name">title</label>
        <input
            type="text"
            name="title"
            id="title"
            value={product.title}
            onChange={handleChange} />

        <label htmlFor="description">description</label>
        <input
            type="text"
            name="description"
            id="description"
            value={product.description}
            onChange={handleChange} />

        <label htmlFor="location">location</label>
        <input
            type="text"
            name="location"
            id="location"
            value={product.location}
            onChange={handleChange} />

        <label htmlFor="category">category</label>
        <input
            type="text"
            name="category"
            id="category"
            value={product.category}
            onChange={handleChange} />

        <label htmlFor="contactInfo">contact info</label>
        <input
            type="text"
            name="contactInfo"
            id="contactInfo"
            value={product.contactInfo}
            onChange={handleChange} />



        <input type="button" value="Submit" onClick={submitForm} />


        </form>



    ); 

}
export default Form;
