import React, {useState} from 'react';
import Button from 'react-bootstrap/Button';
import Image  from "react-bootstrap/Image";
import Card from 'react-bootstrap/Card';

function RenderCards(props) {

    const cards = props.productData.map((product, index) => {
        return (
            <Card style = {{width: "18rem", 
            height: "35vh",   
            background: "#ffffff",
            radius: "10px 10px 10px 10px",
            overflow: "hidden",
            shadow: "0px 12px 18px -6px rgba(0, 0, 0, 0.3)",
            boxshadow: "0px 12px 18px -6px rgba(0, 0, 0, 0.3)",
            margin: "20px"}} 
            key={index} className="box"> 
            <Card.Img variant="top" src="holder.js/100px20" src={product.image} />
                <Card.Body>
                    <Card.Header>{product.title}</Card.Header>
                    <Card.Text>{product.description}</Card.Text>
                </Card.Body>
            </Card>
        );
    });

    return (
        <div>
            {cards}
        </div>
    );
}

function Cards(props) {
    return (
        <div className="grid">
            <RenderCards productData = {props.productData} />
        </div>
    );
}


export default Cards;
